import React, {useState} from 'react';
import sample_figure from './figure.jpg';
import MultiSelect from "react-multi-select-component";
import StatefulInput from "./StatefulInput";

const attributes = {
    "M":    "Move",
    "WS":   "WeaponSkill",
    "BS":   "Balistic Skill",
    "S":    "Strength",
    "T":    "Toughness",
    "W":    "Wounds",
    "A":    "Attacks",
    "Ld":   "Leadership",
    "Sv":   "Save",
}

function Roster() {
    let headers = Object.entries(attributes).map( ([k,v]) => <th>{v} ({k})</th>);
    let formContent = Object.entries(attributes).map( ([k,v]) => {
        return <label htmlFor={k} />;
        /*
        return (
            <>
                <label htmlFor={k} />,
                <input type="number" id={k} name={k} />
            </>
        );
        */
    })
    console.log(formContent)
    return (
        <div>
            <table>
                <thead>
                <tr>{headers}</tr>
                </thead>
            </table>
            <form>
                {formContent}
            </form>
        </div>
    );
}

export default Roster;
