import React, {useState} from 'react';
import sample_figure from './figure.jpg';
import MultiSelect from "react-multi-select-component";
import StatefulInput from "./StatefulInput";

const attributes = {
    "":     {'desc': "Name", 'type': 'text', },
    "M":    {'desc': "Move", 'type': 'number', },
    "WS":   {'desc': "WeaponSkill", 'type': 'number', },
    "BS":   {'desc': "Balistic Skill", 'type': 'number', },
    "S":    {'desc': "Strength", 'type': 'number', },
    "T":    {'desc': "Toughness", 'type': 'number', },
    "W":    {'desc': "Wounds", 'type': 'number', },
    "A":    {'desc': "Attacks", 'type': 'number', },
    "Ld":   {'desc': "Leadership", 'type': 'number', },
    "Sv":   {'desc': "Save", 'type': 'number', },
}

function Roster() {
    let headers = Object.entries(attributes).map( ([k,v]) => <th>{v.desc} ({k})</th>);
    let formContent = Object.entries(attributes).map( ([k,v]) => {
        return (
            <div className="pure-control-group">
                <label htmlFor={k}>{v.desc}</label>
                <input type={v.type} id={k} name={k} />
            </div>
        );
    })
    console.log(formContent)
    return (
        <div>
            <table>
                <thead>
                <tr>{headers}</tr>
                </thead>
            </table>
            <h2>Add Profile:</h2>
            <form className="pure-form pure-form-aligned">
                {formContent}
            </form>
        </div>
    );
}

export default Roster;
