import React, {useState} from 'react';
import sample_figure from './figure.jpg';
import MultiSelect from "react-multi-select-component";
import StatefulInput from "./StatefulInput";

const attributes = {
    "":     "Name",
    "M":    "Move",
    "WS":   "WeaponSkill",
    "BS":   "Balistic Skill",
    "S":    "Strength",
    "T":    "Toughness",
    "W":    "Wounds",
    "A":    "Attacks",
    "Ld":   "Leadership",
    "Sv":   "Save",
}

function Roster() {
    let headers = Object.entries(attributes).map( ([k,v]) => <th>{v} ({k})</th>);
    let formContent = Object.entries(attributes).map( ([k,v]) => {
        return (
            <div className="pure-control-group">
                <label htmlFor={k}>{v}</label>
                <input type="number" id={k} name={k} />
            </div>
        );
    })
    console.log(formContent)
    return (
        <div>
            <table>
                <thead>
                <tr>{headers}</tr>
                </thead>
            </table>
            <h2>Add Profile:</h2>
            <form className="pure-form pure-form-aligned">
                <div className="pure-control-group">
                    <label htmlFor="profileName">Name</label>
                    <input type="text" name="profileName" />
                </div>
                {formContent}
            </form>
        </div>
    );
}

export default Roster;
